import { useState, useEffect } from "react";
import Message from "./components/Message";
import Input from "./components/Input";
import "./App.css";
import { FaFileAlt } from "react-icons/fa";

export default function App() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([]);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [conversationId, setConversationId] = useState(null);
  const [savedConversations, setSavedConversations] = useState([]);

  useEffect(() => {
    startNewConversation();
  }, []);

  const startNewConversation = async () => {
    const response = await fetch("http://localhost:5000/api/start_conversation", {
      method: "POST",
    });
    const data = await response.json();
    setConversationId(data.conversation_id);
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("conversation_id", conversationId);
    formData.append("file", file);

    try {
      const response = await fetch("http://localhost:5000/api/upload_file", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      setUploadedFiles([...uploadedFiles, { name: file.name, icon: <FaFileAlt size={18} /> }]);
      alert(data.message);
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  const handleSubmit = async () => {
    const userMessage = {
      role: "user",
      content: input,
      attachedFiles: uploadedFiles.map(file => file.name),
    };
    setMessages([...messages, userMessage]);
    setInput("");

    try {
      const response = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ user_input: input, conversation_id: conversationId }),
      });
      const data = await response.json();
      setMessages((prev) => [...prev, { role: "assistant", content: data.response }]);
      setUploadedFiles([]);  // Clear uploaded files after sending
    } catch (error) {
      console.error("Error during fetch:", error);
    }
  };

  return (
    <div className="App">
      <div className="Main">
        <div className="Content">
          {messages.map((el, i) => (
            <div key={i}>
              <Message role={el.role} content={el.content} />
              {el.attachedFiles && el.attachedFiles.map((file, index) => (
                <div key={index} className="AttachedFile">
                  <FaFileAlt size={18} /> {file}
                </div>
              ))}
            </div>
          ))}
        </div>

        <Input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onClick={handleSubmit}
          onFileUpload={handleFileUpload}
        />
      </div>
    </div>
  );
}
