import { useState, useEffect } from "react";
import Message from "./components/Message";
import Input from "./components/Input";
import "./App.css";
import { FaFileAlt } from "react-icons/fa";  // File icon import

export default function App() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState(() => {
    const savedMessages = localStorage.getItem("messages");
    return savedMessages ? JSON.parse(savedMessages) : [];
  });
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [savedConversations, setSavedConversations] = useState(() => {
    const saved = localStorage.getItem("savedConversations");
    return saved ? JSON.parse(saved) : [];
  });
  const [conversationId, setConversationId] = useState(null);
  const [loading, setLoading] = useState(false);  // Loader state

  useEffect(() => {
    const initializeConversation = async () => {
      const response = await fetch("http://localhost:5000/api/start_conversation", {
        method: "POST",
      });
      const data = await response.json();
      setConversationId(data.conversation_id);
    };

    if (!conversationId) {
      initializeConversation();
    }
  }, [conversationId]);

  useEffect(() => {
    localStorage.setItem("messages", JSON.stringify(messages));
  }, [messages]);

  const handleSubmit = async () => {
    if (!conversationId) {
      alert("Conversation not initialized properly. Please try again.");
      return;
    }

    const userMessage = {
      role: "user",
      content: input,
      attachedFiles: uploadedFiles.map((file) => file.name),  // Attach file names with message
    };

    setMessages([...messages, userMessage]);
    setLoading(true);  // Show loader

    try {
      const response = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user_input: input, conversation_id: conversationId }),
      });

      const data = await response.json();
      const assistantMessage = { role: "assistant", content: data.response };

      setMessages((prev) => [...prev, assistantMessage]);
      setInput("");
    } catch (error) {
      console.error("Error during fetch:", error);
    } finally {
      setLoading(false);  // Hide loader
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("conversation_id", conversationId);
    formData.append("file", file);

    try {
      const response = await fetch("http://localhost:5000/api/upload_file", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setUploadedFiles((prev) => [...prev, { name: data.file_name, icon: <FaFileAlt size={18} /> }]);
      alert(data.message);
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  const saveAndStartNewConversation = async () => {
    const timestamp = new Date().toLocaleString();
    const savedConversation = {
      id: conversationId,
      title: `Conversation at ${timestamp}`,
      messages: JSON.parse(JSON.stringify(messages)), // Prevent circular reference issues
      files: uploadedFiles,  // Store references to uploaded files
    };

    setSavedConversations((prev) => [...prev, savedConversation]);
    localStorage.setItem("savedConversations", JSON.stringify([...savedConversations, savedConversation]));
    alert("Conversation saved!");

    setMessages([]);
    setUploadedFiles([]);
    const response = await fetch("http://localhost:5000/api/start_conversation", {
      method: "POST",
    });
    const data = await response.json();
    setConversationId(data.conversation_id);
  };

  return (
    <div className="App">
      <div className="Sidebar">
        <h3>Saved Conversations</h3>
        {savedConversations.map((conversation) => (
          <div key={conversation.id} className="ConversationItem">
            {conversation.title}
          </div>
        ))}
      </div>

      <div className="Main">
        <div className="Column">
          <h3 className="Title">Chat Messages</h3>
          <div className="Content">
            {messages.map((el, i) => (
              <div key={i} className="MessageWrapper">
                <Message role={el.role} content={el.content} />
                {el.attachedFiles && el.attachedFiles.map((file, index) => (
                  <div key={index} className="AttachedFile">
                    <FaFileAlt size={18} /> {file} -
                    <a href={`http://localhost:5000/uploaded_files/${file}`} target="_blank" rel="noopener noreferrer">Download</a>
                  </div>
                ))}
              </div>
            ))}
            {loading && <div className="Loader">...</div>}
          </div>

          <Input
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onClick={input ? handleSubmit : undefined}
            onFileUpload={handleFileUpload}
            onEnter={input ? handleSubmit : undefined}
          />

          <button onClick={saveAndStartNewConversation} className="SaveButton">Save and Start New Conversation</button>
        </div>
      </div>
    </div>
  );
}
