import React, { useEffect, useState } from "react";
import "./Input.module.css";
import { FaPaperclip } from "react-icons/fa";

export default function Input({ isSystemPrompt, value, onChange, onClick, onFileUpload, onEnter }) {
  const [placeholder, setPlaceholder] = useState("System Prompt");

  useEffect(() => {
    setPlaceholder(isSystemPrompt ? "System Prompt (e.g., Provide formal responses)" : "Ask a question");
  }, [isSystemPrompt]);

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && onEnter) {
      onEnter();
    }
  };

  return (
    <div className="inputContainer">
      <input
        className="inputField"
        type="text"
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        onKeyDown={handleKeyDown}
      />
      <label className="fileUploadIcon">
        <FaPaperclip size={20} />
        <input type="file" style={{ display: "none" }} onChange={onFileUpload} />
      </label>
      <button className="sendButton" onClick={onClick} disabled={!value.trim()}>Send</button>
    </div>
  );
}
