import styles from "./Input.module.css";

export default function Input({ value, onChange, onClick, onEnter }) {
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      onEnter();  // Trigger onEnter when Enter key is pressed
    }
  };

  return (
    <div className={styles.inputWrapper}>
      <input
        type="text"
        placeholder="Enter your message..."
        value={value}
        onChange={onChange}
        onKeyPress={handleKeyPress}
        className={styles.inputField}
      />
      <button onClick={onClick} className={styles.sendButton}>Send</button>
    </div>
  );
}
