import { useState, useEffect } from "react";
import Message from "./components/Message";
import Input from "./components/Input";
import "./App.css";

export default function App() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState(() => {
    const savedMessages = localStorage.getItem("messages");
    return savedMessages ? JSON.parse(savedMessages) : [];
  });
  const [savedConversations, setSavedConversations] = useState(() => {
    const saved = localStorage.getItem("savedConversations");
    return saved ? JSON.parse(saved) : [];
  });

  useEffect(() => {
    localStorage.setItem("messages", JSON.stringify(messages));
  }, [messages]);

  const handleSubmit = async () => {
    const userMessage = { role: "user", content: input };
    setMessages([...messages, userMessage]);

    try {
      const response = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ session_id: "default", user_input: input }),
      });

      const data = await response.json();
      const assistantMessage = { role: "assistant", content: data.response };

      setMessages((prev) => [...prev, assistantMessage]);
      setInput("");
    } catch (error) {
      console.error("Error during fetch:", error);
    }
  };

  const saveConversation = async () => {
    const response = await fetch("http://localhost:5000/api/save", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ session_id: "default" }),
    });

    const data = await response.json();
    if (data.message === "Conversation saved.") {
      const savedConversation = {
        id: Date.now(),
        title: `Conversation at ${new Date().toLocaleString()}`,
        memory: data.data.memory,
      };

      setSavedConversations((prev) => [...prev, savedConversation]);
      localStorage.setItem("savedConversations", JSON.stringify([...savedConversations, savedConversation]));
      alert("Conversation saved!");
    }
  };

  const loadConversation = async (conversation) => {
    setMessages(conversation.memory.chat_memory.messages);

    await fetch("http://localhost:5000/api/load", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ session_id: "default", saved_memory: conversation.memory }),
    });
  };

  return (
    <div className="App">
      <div className="Sidebar">
        <h3>Saved Conversations</h3>
        {savedConversations.map((conversation) => (
          <div
            key={conversation.id}
            className="ConversationItem"
            onClick={() => loadConversation(conversation)}
          >
            {conversation.title}
          </div>
        ))}
      </div>

      <div className="Main">
        <div className="Column">
          <h3 className="Title">Chat Messages</h3>
          <div className="Content">
            {messages.map((el, i) => (
              <Message key={i} role={el.role} content={el.content} />
            ))}
          </div>
          <Input
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onClick={input ? handleSubmit : undefined}
          />
          <div className="ButtonGroup">
            <button onClick={saveConversation} className="SaveButton">Save Conversation</button>
          </div>
        </div>
      </div>
    </div>
  );
}
