import { useState, useEffect } from "react";
import Message from "./components/Message";
import Input from "./components/Input";
import "./App.css";

export default function App() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState(() => {
    const savedMessages = localStorage.getItem("messages");
    return savedMessages ? JSON.parse(savedMessages) : [];
  });
  const [savedConversations, setSavedConversations] = useState(() => {
    const saved = localStorage.getItem("savedConversations");
    return saved ? JSON.parse(saved) : [];
  });
  const [conversationId, setConversationId] = useState(null);

  // Automatically start a new conversation on page load if none exists
  useEffect(() => {
    const initializeConversation = async () => {
      const response = await fetch("http://localhost:5000/api/start_conversation", {
        method: "POST",
      });
      const data = await response.json();
      setConversationId(data.conversation_id);
    };

    if (!conversationId) {
      initializeConversation();
    }
  }, [conversationId]);

  useEffect(() => {
    localStorage.setItem("messages", JSON.stringify(messages));
  }, [messages]);

  const handleSubmit = async () => {
    if (!conversationId) {
      alert("Conversation not initialized properly. Please try again.");
      return;
    }

    const userMessage = { role: "user", content: input };
    setMessages([...messages, userMessage]);

    try {
      const response = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user_input: input, conversation_id: conversationId }),
      });

      const data = await response.json();
      const assistantMessage = { role: "assistant", content: data.response };

      setMessages((prev) => [...prev, assistantMessage]);
      setInput("");
    } catch (error) {
      console.error("Error during fetch:", error);
    }
  };

  const saveAndStartNewConversation = async () => {
    const timestamp = new Date().toLocaleString();
    const existingConversationIndex = savedConversations.findIndex(
      (conversation) => conversation.id === conversationId
    );

    const savedMessages = messages.map((message) => {
      if (message.role === "user") {
        return { user: message.content, assistant: "" };
      } else {
        return { user: "", assistant: message.content };
      }
    });

    if (existingConversationIndex > -1) {
      // Update existing conversation
      const updatedConversations = [...savedConversations];
      updatedConversations[existingConversationIndex].messages = savedMessages;
      setSavedConversations(updatedConversations);
      localStorage.setItem("savedConversations", JSON.stringify(updatedConversations));
    } else {
      // Save as a new conversation
      const newConversation = {
        id: conversationId,
        title: `Conversation at ${timestamp}`,
        messages: savedMessages,
      };

      const updatedConversations = [...savedConversations, newConversation];
      setSavedConversations(updatedConversations);
      localStorage.setItem("savedConversations", JSON.stringify(updatedConversations));
    }

    alert("Conversation saved!");

    // Automatically start a new conversation
    setMessages([]);
    const response = await fetch("http://localhost:5000/api/start_conversation", {
      method: "POST",
    });
    const data = await response.json();
    setConversationId(data.conversation_id);
  };

  const loadConversation = async (conversation) => {
    setConversationId(conversation.id);
    setMessages(conversation.messages.map((msg) => {
      if (msg.user) {
        return { role: "user", content: msg.user };
      } else {
        return { role: "assistant", content: msg.assistant };
      }
    }));

    // Send conversation to backend to load into LangChain memory
    await fetch("http://localhost:5000/api/load_conversation", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        conversation_id: conversation.id,
        messages: conversation.messages,
      }),
    });
  };

  return (
    <div className="App">
      <div className="Sidebar">
        <h3>Saved Conversations</h3>
        {savedConversations.map((conversation) => (
          <div
            key={conversation.id}
            className="ConversationItem"
            onClick={() => loadConversation(conversation)}
          >
            {conversation.title}
          </div>
        ))}
      </div>

      <div className="Main">
        <div className="Column">
          <h3 className="Title">Chat Messages</h3>
          <div className="Content">
            {messages.map((el, i) => (
              <Message key={i} role={el.role} content={el.content} />
            ))}
          </div>
          <Input
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onClick={input ? handleSubmit : undefined}
          />
          <div className="ButtonGroup">
            <button onClick={saveAndStartNewConversation} className="SaveButton">
              Save and Start New Conversation
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
