import { useState, useEffect } from "react";
import Message from "./components/Message";
import Input from "./components/Input";
import Clear from "./components/Clear";
import { v4 as uuidv4 } from "uuid";  // Generate session IDs
import "./App.css";

export default function App() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([]);
  const [savedConversations, setSavedConversations] = useState(() => {
    const saved = localStorage.getItem("savedConversations");
    return saved ? JSON.parse(saved) : [];
  });
  const [currentSessionId, setCurrentSessionId] = useState(() => uuidv4());

  const handleSubmit = async () => {
    const userMessage = { role: "user", content: input };
    setMessages([...messages, userMessage]);

    try {
      const response = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          session_id: currentSessionId,
          user_input: input,
          model: "gpt-3.5-turbo"
        }),
      });

      const data = await response.json();
      const assistantMessage = { role: "assistant", content: data.response };

      setMessages((prev) => [...prev, assistantMessage]);
      setInput("");
    } catch (error) {
      console.error("Error during fetch:", error);
    }
  };

  const saveConversation = () => {
    const timestamp = new Date().toLocaleString();
    const newConversation = {
      sessionId: currentSessionId,
      title: `Conversation at ${timestamp}`,
      messages
    };

    setSavedConversations([...savedConversations, newConversation]);
    alert("Conversation saved!");
  };

  const loadConversation = (conversation) => {
    setCurrentSessionId(conversation.sessionId);
    setMessages(conversation.messages);
  };

  return (
    <div className="App">
      <div className="Sidebar">
        <h3>Saved Conversations</h3>
        {savedConversations.map((conversation) => (
          <div
            key={conversation.sessionId}
            className="ConversationItem"
            onClick={() => loadConversation(conversation)}
          >
            {conversation.title}
          </div>
        ))}
      </div>

      <div className="Main">
        <div className="Column">
          <h3 className="Title">Chat Messages</h3>
          <div className="Content">
            {messages.map((el, i) => (
              <Message key={i} role={el.role} content={el.content} />
            ))}
          </div>
          <Input
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onClick={input ? handleSubmit : undefined}
          />
          <div className="ButtonGroup">
            <button onClick={saveConversation} className="SaveButton">Save Conversation</button>
            <Clear onClick={() => setMessages([])} />
          </div>
        </div>
      </div>
    </div>
  );
}
