import styles from "./Input.module.css";
import { FaPaperclip } from "react-icons/fa"; // Pin icon import

export default function Input({ value, onChange, onClick }) {
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      alert(`File uploaded: ${file.name}`); // Replace with your upload logic
    }
  };

  // Trigger the onClick function when Enter is pressed
  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      event.preventDefault();
      onClick(); // Trigger message submission
    }
  };

  return (
    <div className={styles.wrapper}>
      <input
        className={styles.text}
        placeholder="Your prompt here..."
        value={value}
        onChange={onChange}
        onKeyPress={handleKeyPress}  // Detect Enter key press
      />
      <label htmlFor="file-upload" className={styles.pin}>
        <FaPaperclip size={20} />
      </label>
      <input
        id="file-upload"
        type="file"
        style={{ display: "none" }}
        onChange={handleFileUpload}
      />
      <button className={styles.btn} onClick={onClick}>
        Go
      </button>
    </div>
  );
}
