import { useState, useEffect } from "react";
import Message from "./components/Message";
import Input from "./components/Input";
import "./App.css";
import { FaFileAlt, FaCheckCircle } from "react-icons/fa";  // File icon import

export default function App() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState(() => {
    const savedMessages = localStorage.getItem("messages");
    return savedMessages ? JSON.parse(savedMessages) : [];
  });
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [savedConversations, setSavedConversations] = useState(() => {
    const saved = localStorage.getItem("savedConversations");
    return saved ? JSON.parse(saved) : [];
  });
  const [conversationId, setConversationId] = useState(null);
  const [loading, setLoading] = useState(false);  // Loader state
  const [currentConversationIndex, setCurrentConversationIndex] = useState(null); // Track current conversation
  const [fileUploaded, setFileUploaded] = useState(false);  // Track if a file has been uploaded
  const [errorMessage, setErrorMessage] = useState("");  // Track file upload errors
  const [isSystemPrompt, setIsSystemPrompt] = useState(true);  // Track if current prompt is system prompt

  useEffect(() => {
    const initializeConversation = async () => {
      const response = await fetch("http://localhost:5000/api/start_conversation", {
        method: "POST",
      });
      const data = await response.json();
      setConversationId(data.conversation_id);
    };

    if (!conversationId) {
      initializeConversation();
    }
  }, [conversationId]);

  useEffect(() => {
    localStorage.setItem("messages", JSON.stringify(messages));
  }, [messages]);

  const handleSubmit = async () => {
    if (!conversationId) {
      alert("Conversation not initialized properly. Please try again.");
      return;
    }

    const userMessage = {
      role: "user",
      content: input,
      isSystemPrompt: isSystemPrompt,  // Track if this is the initial system prompt
    };

    setMessages([...messages, userMessage]);
    setInput("");  // Clear input as soon as message is submitted

    // Skip response if it's a system prompt
    if (isSystemPrompt) {
      setIsSystemPrompt(false);  // Switch to regular prompts after the system prompt
      return;
    }

    setLoading(true);  // Show loader

    try {
      const formData = new FormData();
      uploadedFiles.forEach(file => formData.append("files", file));
      formData.append("conversation_id", conversationId);
      formData.append("user_input", input);

      const response = await fetch("http://localhost:5000/api/chat_with_files", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      const assistantMessage = { role: "assistant", content: data.response };

      setMessages((prev) => [...prev, assistantMessage]);
      setUploadedFiles([]);  // Clear files after the query is done
      setFileUploaded(false);  // Reset file upload indicator
      setErrorMessage("");  // Clear any previous error messages
    } catch (error) {
      console.error("Error during fetch:", error);
    } finally {
      setLoading(false);  // Hide loader
    }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (file.type !== "application/pdf") {
      setErrorMessage("Only PDF files are allowed.");
      return;
    }

    setUploadedFiles((prev) => [...prev, file]);
    setFileUploaded(true);  // Indicate that a file has been uploaded
    setErrorMessage("");  // Clear any previous error messages
  };

  const saveAndStartNewConversation = async () => {
    const timestamp = new Date().toLocaleString();

    if (currentConversationIndex !== null) {
      // Update the existing conversation
      const updatedConversations = [...savedConversations];
      updatedConversations[currentConversationIndex].messages = messages.map(msg => ({ role: msg.role, content: msg.content }));
      updatedConversations[currentConversationIndex].title = `Updated at ${timestamp}`;
      setSavedConversations(updatedConversations);
      localStorage.setItem("savedConversations", JSON.stringify(updatedConversations));
      alert("Conversation updated!");
    } else {
      // Save as a new conversation
      const newConversation = {
        id: conversationId,
        title: `Conversation at ${timestamp}`,
        messages: messages.map(msg => ({ role: msg.role, content: msg.content })),  // Exclude files
      };
      setSavedConversations((prev) => [...prev, newConversation]);
      localStorage.setItem("savedConversations", JSON.stringify([...savedConversations, newConversation]));
      alert("Conversation saved!");
    }

    // Start a new conversation
    setMessages([]);
    setUploadedFiles([]);
    setIsSystemPrompt(true);  // Reset to system prompt for new conversation
    const response = await fetch("http://localhost:5000/api/start_conversation", {
      method: "POST",
    });
    const data = await response.json();
    setConversationId(data.conversation_id);
    setCurrentConversationIndex(null);  // Reset for new conversation
  };

  const loadConversation = (conversation, index) => {
    setMessages(conversation.messages);
    setConversationId(conversation.id);
    setCurrentConversationIndex(index);  // Track current conversation
    setIsSystemPrompt(conversation.messages.length === 0);  // Determine if it's a new or continued conversation
  };

  return (
    <div className="App">
      <div className="Sidebar">
        <h3>Saved Conversations</h3>
        {savedConversations.map((conversation, index) => (
          <div
            key={conversation.id}
            className="ConversationItem"
            onClick={() => loadConversation(conversation, index)}
          >
            {conversation.title}
          </div>
        ))}
      </div>

      <div className="Main">
        <div className="Column">
          <div className="Content">
            {messages.map((el, i) => (
              <div key={i} className="MessageWrapper">
                <Message role={el.role} content={el.content} />
              </div>
            ))}
            {loading && <div className="Loader">...</div>}
            {fileUploaded && <div className="FileIndicator"><FaCheckCircle color="green" /> File Uploaded</div>}
            {errorMessage && <div className="ErrorIndicator">{errorMessage}</div>}
          </div>

          <Input
            placeholder={isSystemPrompt ? "System Prompt" : "Ask a question"}
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onClick={input ? handleSubmit : undefined}
            onFileUpload={handleFileUpload}
            onEnter={input ? handleSubmit : undefined}
          />

          <button onClick={saveAndStartNewConversation} className="SaveButton">Save and Start New Conversation</button>
        </div>
      </div>
    </div>
  );
}
