import { useState } from "react";
import "./Input.module.css";
import { FaPaperclip } from "react-icons/fa";  // File attachment icon

export default function Input({ value, onChange, onClick, onEnter, onFileUpload, placeholder }) {
  const [fileInputKey, setFileInputKey] = useState(Date.now());  // For resetting file input

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && onEnter) {
      onEnter();
    }
  };

  const handleFileChange = (e) => {
    onFileUpload(e);
    setFileInputKey(Date.now());  // Reset file input after upload
  };

  return (
    <div className="input-container">
      <input
        type="text"
        className="input-box"
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        onKeyPress={handleKeyPress}
      />
      <div className="input-icons">
        <label htmlFor="file-upload">
          <FaPaperclip size={20} />
        </label>
        <input
          id="file-upload"
          type="file"
          key={fileInputKey}  // Reset file input
          onChange={handleFileChange}
          style={{ display: "none" }}
        />
      </div>
      <button onClick={onClick} className="send-button">
        Send
      </button>
    </div>
  );
}
